package cn.com.payu.app.modules.utils;

import cn.com.payu.app.common.exception.AppServerException;
import cn.com.payu.app.config.SenseTimeConfig;
import cn.com.payu.app.modules.model.sensetime.req.AbstractSenseTimeReq;
import cn.hutool.http.HttpRequest;
import com.alibaba.fastjson.JSONObject;
import com.glsx.plat.common.utils.HttpUtils;
import com.glsx.plat.common.utils.ObjectUtils;
import lombok.extern.slf4j.Slf4j;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.HttpMethod;
import org.springframework.stereotype.Component;

import java.security.SignatureException;

@Slf4j
@Component
public class SenseTimeUtils {

    @Autowired
    private SenseTimeConfig senseTimeConfig;

    /**
     * 调用服务
     */
    public <T> T accessByPost(String subRoute, Object req, Class<T> clazz) {
        String url = senseTimeConfig.getBaseApiUrl() + subRoute;
        return accessUrl(url, req, clazz, HttpMethod.POST);
    }

    /**
     * 调用服务
     */
    public <T> T accessByGet(String subRoute, Object req, Class<T> clazz) {
        String url = senseTimeConfig.getBaseApiUrl() + subRoute;
        return accessUrl(url, req, clazz, HttpMethod.GET);
    }

    /**
     * 调用商汤
     */
    private <T> T accessUrl(String url, Object req, Class<T> clazz, HttpMethod method) throws AppServerException {

        AbstractSenseTimeReq paramReq = (AbstractSenseTimeReq) req;

        // 1. 补全请求数据，封装参数
        complementedRequestData(paramReq);

        // 2. 校验请求数据
        verifyRequestData(paramReq);

        // 3. 网络请求,获取结果
        return realCall(url, paramReq, clazz, method);
    }

    /**
     * 补全请求实体—— 补全通用的实体属性,比如appkey属性及sign属性。
     * 也可以通过 instanceof,针对特定请求实体进行补全操作。
     */
    private void complementedRequestData(AbstractSenseTimeReq paramReq) throws AppServerException {

    }


    /**
     * 校验请求实体的通用属性,比如appkey属性及sign属性。
     * 也可以通过 instanceof,针对特定请求实体进行校验操作。
     */
    private void verifyRequestData(AbstractSenseTimeReq req) {

    }

    /**
     * 真正调用网络请求方法
     *
     * @param url
     * @param paramReq
     * @param <T>
     * @return
     */
    private <T> T realCall(String url, AbstractSenseTimeReq paramReq, Class<T> clazz, HttpMethod method) throws AppServerException {

        String body = JSONObject.toJSONString(paramReq);
        log.info("【商汤】http请求信息—— url[{}] params[{}]", url, body);
        //发送post请求
        String result = null;
        try {
            if (HttpMethod.POST.equals(method)) {
                result = HttpRequest.post(url)
                        //设置请求头
                        .header("Content-Type", "application/json")
                        //这两个请求头是项目需要加的，可以省略
                        .header("Authorization", GenerateString.genHeaderParam(senseTimeConfig.getKeyId(), senseTimeConfig.getKeySecret()))
                        //传输参数
                        .body(body)
                        .execute()
                        .body();
            } else {
                result = HttpUtils
                        .createGet(url + "?" + HttpUtils.toParams(ObjectUtils.object2Map(paramReq)))
                        .execute()
                        .body();
//                result = "{\"code\":1000,\"liveness_mode\":\"interactive\",\"passed\":true,\"liveness_status\":\"ok\",\"biz_no\":\"490053996619300864\",\"biz_extra_data\":null,\"base64_liveness_image\":\"\",\"base64_liveness_image_sign\":\"a2V5PWFjMzE4YWEwMGFlYTRiZjY4YWVkMTI5MTRhMWNmNDlkLHRpbWVzdGFtcD0xNjQ3NDQ1OTE1MTc5LG5vbmNlPWZmZGY4ZGEyLWZiYjItNDU2Yi05NWQ4LTQzNzRkYTljNzY0YixzaWduX3Zlcj1DbG91ZEJsb2JWMSxzaWduYXR1cmU9MDA4MmU2OTdlMGUxYzMzYmVhYmQ0MTJiNTBiZjMzZGI5Y2VmMWM1ZjZkYmI0ZGMzMWFmNzViYzgwNTQzM2M3Nw==\",\"base64_liveness_face_image\":null,\"base64_liveness_face_image_sign\":null,\"request_id\":\"5e93e925ad1c1cabf36b7eac1fd6d0fc\"}";
            }
        } catch (SignatureException e) {
            throw new AppServerException(e.getMessage());
        } catch (IllegalAccessException e) {
            e.printStackTrace();
        }
        log.info("【商汤】http响应信息—— result[{}]", result);

        return JSONObject.parseObject(result, clazz);
    }

}
