mapper.select:
select to2.id, to2.order_no orderNo, to2.created_date createdDate, to2.order_type orderType, tcup.real_name realName, to2.amount, tcu.account, tcu.from_app fromApp, tc.channel_name as channel from t_order to2 left join t_cust_user tcu on tcu.id = to2.cuser_id left join t_cust_user_profile tcup on tcup.user_id = tcu.id left join t_channel tc on tc.channel = tcu.channel where to2.status = 1
and to2.order_no like CONCAT('%',#{orderNo},'%')
and to2.order_type = #{orderType}
and tcu.from_app = #{fromApp}
and tcu.channel = #{channel}
and tcup.real_name like CONCAT('%',#{username},'%')
and DATE_FORMAT(to2.created_date, '%Y-%m-%d') >= #{sDate} and DATE_FORMAT(to2.created_date, '%Y-%m-%d') <= #{eDate}
order by to2.created_date desc

select to2.id, to2.order_no orderNo, to2.created_date createdDate, to2.order_type orderType, tcup.real_name realName, to2.amount, tcu.account, tcu.from_app fromApp, tc.channel_name as channel from t_order to2 left join t_cust_user tcu on tcu.id = to2.cuser_id left join t_cust_user_profile tcup on tcup.user_id = tcu.id left join t_channel tc on tc.channel = tcu.channel where to2.status = 1
and to2.order_no like CONCAT('%',#{orderNo},'%')
and to2.order_type = #{orderType}
and tcu.from_app = #{fromApp}
and tcu.channel = #{channel}
and tcup.real_name like CONCAT('%',#{username},'%')
and DATE_FORMAT(to2.created_date, '%Y-%m-%d') >= #{sDate} and DATE_FORMAT(to2.created_date, '%Y-%m-%d') <= #{eDate}
order by to2.created_date desc

select tcu.channel label, count(to2.id) cnt, sum(to2.amount) amount from t_order to2 left join t_cust_user tcu on tcu.id = to2.cuser_id where to2.order_type = 80 and to2.status = 1
and tcu.channel = #{channel}
group by tcu.channel

select tog.goods_code goodsCode, any_value(tog.goods_name) goodsName, count(to2.id) orderCnt, sum(amount) orderAmount from t_order to2 left join t_order_goods tog on tog.order_id = to2.id where to2.status = 1 and tog.order_id is not null and to2.order_type = 80
and DATE_FORMAT(to2.created_date, '%Y-%m-%d') = #{statDate}
group by tog.goods_code
