mapper.select:
select tp.id, name productName, periods, tp.unit_price unitPrice, day_interest_rate dayInterestRate, max_amount maxAmount, type, remark, order_num orderNum, enable_status enableStatus, tp.created_date createdDate, tpd.clickCnt from t_product tp left join (select product_id, count(user_id) clickCnt from t_product_drainage itpd where itpd.del_flag = 0
and DATE_FORMAT(itpd.created_date, '%Y-%m-%d') >= #{sDate} and DATE_FORMAT(itpd.created_date, '%Y-%m-%d') <= #{eDate}
group by product_id) tpd on tpd.product_id = tp.id where tp.del_flag = 0
and tp.name like CONCAT('%',#{productName},'%')
and tp.enable_status = #{enableStatus}
order by tp.order_num

select tp.id, name productName, periods, day_interest_rate dayInterestRate, max_amount maxAmount, type, tp.tags, remark, order_num orderNum, case when enable_status=1 then '已上架' else '已下架' end enableStatus, tp.created_date createdDate, tpd.clickCnt from t_product tp left join (select product_id, count(user_id) clickCnt from t_product_drainage group by product_id) tpd on tpd.product_id = tp.id where tp.del_flag = 0
and tp.name like CONCAT('%',#{productName},'%')
and tp.enable_status = #{enableStatus}
and DATE_FORMAT(tp.created_date, '%Y-%m-%d') >= #{sDate} and DATE_FORMAT(tp.created_date, '%Y-%m-%d') <= #{eDate}
order by tp.order_num

mapper.update:
update t_product
set enable_status = #{enableStatus}
where id = #{id}

update t_product
set used_cnt = used_cnt + 1
where id = #{id}
